'use strict';

const httpStatusCodes = require('../http-status-codes.cjs');
const httpStatusPhrases = require('../http-status-phrases.cjs');

const notFound = (c) => {
  return c.json({
    message: `${httpStatusPhrases.NOT_FOUND} - ${c.req.path}`
  }, httpStatusCodes.NOT_FOUND);
};

module.exports = notFound;
