'use strict';

const httpStatusCodes = require('../http-status-codes.cjs');

const defaultHook = (result, c) => {
  if (!result.success) {
    return c.json(
      {
        success: result.success,
        error: result.error
      },
      httpStatusCodes.UNPROCESSABLE_ENTITY
    );
  }
};

module.exports = defaultHook;
