'use strict';

const openapi_helpers_oneOf = require('./one-of.cjs');
require('@asteasolutions/zod-to-openapi');

function jsonContentOneOf(schemas, description) {
  return {
    content: {
      "application/json": {
        schema: {
          oneOf: openapi_helpers_oneOf(schemas)
        }
      }
    },
    description
  };
}

module.exports = jsonContentOneOf;
