import oneOf from './one-of.mjs';
import '@asteasolutions/zod-to-openapi';

function jsonContentOneOf(schemas, description) {
  return {
    content: {
      "application/json": {
        schema: {
          oneOf: oneOf(schemas)
        }
      }
    },
    description
  };
}

export { jsonContentOneOf as default };
