'use strict';

const zodOpenapi = require('@hono/zod-openapi');

const IdUUIDParamsSchema = zodOpenapi.z.object({
  id: zodOpenapi.z.string().uuid().openapi({
    param: {
      name: "id",
      in: "path"
    },
    required: ["id"],
    example: "4651e634-a530-4484-9b09-9616a28f35e3"
  })
});

module.exports = IdUUIDParamsSchema;
