'use strict';

const zodOpenapi = require('@hono/zod-openapi');

const slugReg = /^[\w-]+$/;
const SLUG_ERROR_MESSAGE = "Slug can only contain letters, numbers, dashes, and underscores";
const SlugParamsSchema = zodOpenapi.z.object({
  slug: zodOpenapi.z.string().regex(slugReg, SLUG_ERROR_MESSAGE).openapi({
    param: {
      name: "slug",
      in: "path"
    },
    required: ["slug"],
    example: "my-cool-article"
  })
});

module.exports = SlugParamsSchema;
